sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate) {
		// Create the GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		// If the testDate is before rightnow , return true, otherwise return false
		if (testDate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate) {
		// Create the GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);
		// Get the date portion of rightnow and testDate with no timestamp
		var today = rightnow.getLocalDate();
		var isToday = testDate.getLocalDate();

		// Compare to see if today and isToday are the same day.
		if (today.compareTo(isToday) == 0) {
			return true;
		}
		else {
			return false;
		}

	},

    type: 'NeedItUtils'
};
